15.query:
// list the last stable values for CPU usage, throttling, requests and limits for each namespace
timeseries {
  cpu_usage = sum(dt.kubernetes.container.cpu_usage),
  cpu_throttled = sum(dt.kubernetes.container.cpu_throttled),
  requests_cpu = sum(dt.kubernetes.container.requests_cpu),
  limits_cpu = sum(dt.kubernetes.container.limits_cpu)
}, 
// filter by the cluster selected 
filter: {
  k8s.cluster.name == $Cluster
}, by: { dt.entity.cloud_application_namespace, k8s.namespace.name },
from: -2m,
nonempty: true,
// include result tuples which have no requests or limits defined (shown as null)
union: true
| fieldsRemove interval, timeframe
| fieldsAdd cpu_usage = arrayFirst(cpu_usage)
| fieldsAdd cpu_throttled = arrayFirst(cpu_throttled)
| fieldsAdd requests_cpu = arrayFirst(requests_cpu)
| fieldsAdd limits_cpu = arrayFirst(limits_cpu)
| fieldsAdd requests_cpu_percent = cpu_usage / requests_cpu * 100
| fieldsAdd limits_cpu_percent = cpu_usage / limits_cpu * 100
| fieldsAdd cpu_slack = (requests_cpu - cpu_usage) / 1000
| sort cpu_usage desc
| fieldsRename `Name` = k8s.namespace.name, `CPU Usage` = cpu_usage, `CPU Throttled` = cpu_throttled, `CPU Requests` = requests_cpu, `CPU Requests %` = requests_cpu_percent, `CPU Limits` = limits_cpu, `CPU Limits %` = limits_cpu_percent, `CPU Slack` = cpu_slack

