6.query:
// calculate the last stable CPU usage in percent 
// in relation to the CPU usage of the whole cluster.
// get the last stable total CPU usage of the cluster.
timeseries {
  cpu_usage = sum(dt.kubernetes.container.cpu_usage)
}, filter: {
  // filter by selected cluster
  k8s.cluster.name == $Cluster
}, by: { k8s.cluster.name },
from: -2m
| fieldsAdd cpu_usage_cluster = arrayFirst(cpu_usage)
| fieldsRemove cpu_usage
// get the last stable total CPU usage for the selected namespace in the selected cluster
| join [
  timeseries {
    cpu_usage = sum(dt.kubernetes.container.cpu_usage)
  }, filter: {
    // filter by selected cluster and namespace
    k8s.cluster.name == $Cluster AND
    k8s.namespace.name == $Namespace
  }, by: { k8s.namespace.name, k8s.cluster.name },
  from: -2m
], executionOrder: leftFirst, on: { k8s.cluster.name }, fields: { cpu_usage }
| fieldsAdd cpu_usage_namespace = arrayFirst(cpu_usage)
// CPU usage for this namespace in percent of the whole cluster CPU usage
// the total namespace usages for one cluster should sum up to around 100 percent,
// however percentages might vary over time, and there are rounding differences
| fieldsAdd cpu_usage_percent = cpu_usage_namespace * 100  / cpu_usage_cluster

7.query:
