// calculate to total memory usage of the cluster
timeseries {  
  memory_usage = sum(dt.kubernetes.container.memory_working_set)
}, filter: {
  // filter by selected cluster
  k8s.cluster.name == $Cluster
}, by: { k8s.cluster.name },
from: -2m
| fieldsAdd memory_usage_cluster = arrayFirst(memory_usage)
| fieldsRemove memory_usage
// calculate the total memory usage of the namespace
| join [
  timeseries {
    memory_usage = sum(dt.kubernetes.container.memory_working_set)
  }, filter: {
    // filter by selected cluster and namespace
    k8s.cluster.name == $Cluster AND
    k8s.namespace.name == $Namespace
  }, by: { k8s.namespace.name, k8s.cluster.name },
  from: -2m
], executionOrder: leftFirst, on: { k8s.cluster.name }, fields: { memory_usage }
| fieldsAdd memory_usage_namespace = arrayFirst(memory_usage)
| fieldsAdd memory_usage_percent = memory_usage_namespace * 100  / memory_usage_cluster

8.query:
