timeseries {
  pods_namespace = sum(dt.kubernetes.pods)
}, filter: {
  // filter by selected cluster and namespace
  k8s.cluster.name == $Cluster AND
  k8s.namespace.name == $Namespace AND
  // consider pods in running and pods with an unknown pod phase
  if(isNotNull(pod_phase), pod_phase == "Running", else: true)
}, by: { k8s.namespace.name },
from: -2m
